mapper.insert:
/* SQL_id = user.FNL1001.signUp */
INSERT INTO FNL1001
(
	  MB_IDNO
	, MB_ID
	, MB_PWD
	, MB_CODE
	, MB_REG_DATE
	, MB_CHNG_MBID
	, MB_CHNG_DATE
)
VALUES
(
	  #{mbIdno}
    , #{mbId}
    , #{mbPass}
    , 'REG'
    , SYSDATE
    , #{mbIdno}
    , SYSDATE
)

mapper.select:
/* SQL_id = user.FNL1001.getPwd */
SELECT MB_PWD
  FROM FNL1001
 WHERE MB_ID = #{mbId}

mapper.update:
/* SQL_id = user.FNL1001.updateMbErrCnt */
UPDATE FNL1001
   SET MB_ERR_CNT = MB_ERR_CNT + 1
 WHERE MB_ID = #{mbId}
